package com.syntax.class01;

public class FirstClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte box1 = 21;
		short box2=2022;
		int box3=76666;
		long box4=265467484847646363L;
		
		//decimal numbers
		float container=1.99f;
		double container2=89.99;
		
		// to represent 1 character
		char gender='m';
		char dollar='$';
		
		//to represent true or false (yes or no)
		boolean hungry=true;
		boolean sleepy=false;
		
		//how to access values form variables?
		
		System.out.println(container2);
		System.out.println(box1);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(container);
		System.out.println(gender);
		System.out.println(dollar);
		System.out.println(hungry);
		System.out.println(sleepy);
		
		
		//do not use "" to access variables
		
		// access and print values from all variables
	}

}
